function dragElement(elmnt) {
  var pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;
  if (document.getElementById(elmnt.id + "_titulo")) {
    // if present, the header is where you move the DIV from:
    document.getElementById(elmnt.id + "_titulo").onmousedown = dragMouseDown;
  } else {
    // otherwise, move the DIV from anywhere inside the DIV:
    elmnt.onmousedown = dragMouseDown;
  }

  function dragMouseDown(e) {
    e = e || window.event;
    e.preventDefault();
    // get the mouse cursor position at startup:
	var str = elmnt.style.left;
	var pxstr = str.slice(0,str.length-2);
	pos1 = parseInt( pxstr );

	str = elmnt.style.top;
	pxstr = str.slice(0,str.length-2);
	pos2 = parseInt( pxstr );
	
    document.onmouseup = closeDragElement;
    // call a function whenever the cursor moves:
    document.onmousemove = elementDrag;
  }

  function elementDrag(e) {
    e = e || window.event;
    e.preventDefault();
    // calculate the new cursor position:
	pos1 = pos1 + e.movementX;
	pos2 = pos2 + e.movementY;
	
	console.log("X: " + pos1 + " / Y: " + pos2);
	
	if( pos1 < 0 ){
		pos1 = 0;
	}
	
	if( pos2 < 0 ){
		pos2 = 0;
	}
	
	if(pos1 <= 21){
		pos1 = 21;
	}
	
	if(pos2 <= 153){
		pos2 = 153;
	}
	
	if( pos1 >= 1072 ){
		pos1 = 1072;
	} 
	
	if( pos2 >= 459 ){
		pos2 = 459;
	}
	
    // set the element's new position:	
	if( pos1 > 0 )
		elmnt.style.left = pos1 + "px";
	else
		elmnt.style.left = "0px";
	if( pos2 > 0)
		elmnt.style.top = pos2 + "px";
	else
		elmnt.style.top = "0px";
  }

  function closeDragElement() {
    // stop moving when mouse button is released:
    document.onmouseup = null;
    document.onmousemove = null;
  }
}

function dragElementHorariosAtribuir(elmnt) {
  var pos1 = 0, pos2 = 0, pos3 = 0, pos4 = 0;
  if (document.getElementById(elmnt.id + "_titulo")) {
    // if present, the header is where you move the DIV from:
