function abrirHorarioDocente(){
	
	const select_docente = document.getElementById("select_docente");
	const id_docente_selecionado = select_docente.value;
	
	abrirHorarioDocenteParticular(id_docente_selecionado);
	
}

function abrirHorarioDocenteParticular(id_docente_selecionado){

	//alert(id_sala_selecionada);
	if(id_docente_selecionado != ""){
		//alert("Abrir horário!");
		
		var novo_horario = document.createElement('div');
		novo_horario.setAttribute("id","docente_" + id_docente_selecionado);
		novo_horario.setAttribute("class","horario");
		novo_horario.setAttribute("tipo","docente");
//		novo_horario.setAttribute("onclick","novoCliqueTabela('sala'," + id_sala_selecionada + ")");
				
		novo_horario.style.top = (153 + (janelas_abertas * 15)) + "px";
		novo_horario.style.left = (21 + (janelas_abertas * 15)) + "px";
		
		preencherHorarioDocente(id_docente_selecionado,novo_horario,0);
		
	}
	
}

