function removerHorario(tipo_horario,id){
	
	const select_sala = document.getElementById("select_sala");
	const select_docente = document.getElementById("select_docente");
	const select_turma = document.getElementById("select_turma");
	
	if(tipo_horario == 'sala'){
		var elem = document.getElementById("sala_" + id); 
		elem.parentNode.removeChild(elem);
		
		for(i = 0; i < salas_abertas.length; i++){
			if(salas_abertas[i] == id){
				salas_abertas.splice(i,1);
			}
		}
		
		janelas_abertas -= 1;
		removeOptions(select_sala);
		$.ajax ({
			type: "POST",
			url: "processamento/horarios/verSalas.php", 
