				var array = result.split(',');	
						
				for(i = 0; i < array.length; i = i + 2){
					var opt = document.createElement('option');
					if(salas_abertas.indexOf(array[i]) != -1){
						opt.setAttribute("disabled","disabled");
					}
					opt.value = array[i];
					opt.text = array[i + 1];
					select_sala.options.add(opt);
				} 
			}
		});
	}
	else if(tipo_horario == 'docente'){
		var elem = document.getElementById("docente_" + id); 
		elem.parentNode.removeChild(elem);
		
		for(i = 0; i < docentes_abertos.length; i++){
			if(docentes_abertos[i] == id){
				docentes_abertos.splice(i,1);
			}
		}
		
		janelas_abertas -= 1;
		removeOptions(select_docente);
		$.ajax ({
			type: "POST",
			url: "processamento/horarios/verDocentes.php", 
			data: {id_utc: id_utc},
			success: function(result) {
				var array = result.split(',');	
						
				for(i = 0; i < array.length; i = i + 2){
					var opt = document.createElement('option');
					if(docentes_abertos.indexOf(array[i]) != -1){
						opt.setAttribute("disabled","disabled");
					}
					opt.value = array[i];
					opt.text = array[i + 1];
					select_docente.options.add(opt);
				} 
			}
		});
	}
	else if(tipo_horario == 'turma'){
		var elem = document.getElementById("turma_" + id); 
		elem.parentNode.removeChild(elem);
		
		for(i = 0; i < turmas_abertas.length; i++){
			if(turmas_abertas[i] == id){
				turmas_abertas.splice(i,1);
			}
		}
		
		janelas_abertas -= 1;
		removeOptions(select_turma);
		$.ajax ({
			type: "POST",
			url: "processamento/horarios/verTurmas.php", 
			data: {id_utc: id_utc, semestre_atual: semestre_atual},
			success: function(result) {
				var array = result.split(',');	
						
				var opt_vazia = document.createElement('option');
				opt_vazia.value = "";
				opt_vazia.text = "";
				select_turma.options.add(opt_vazia);
						
				for(i = 0; i < array.length; i = i + 2){
					var opt = document.createElement('option');
					if(turmas_abertas.indexOf(array[i]) != -1){
						opt.setAttribute("disabled","disabled");
					}
					if(array[i] == 'disabled'){
						opt.setAttribute("disabled","disabled");
					}
					opt.value = array[i];
					opt.text = array[i + 1];
					if(array[i] == 'disabled'){
						opt.text = "       " + array[i + 1];
					}
					select_turma.options.add(opt);
				} 
			}
		});
	}
	
	for(i = 0; i < salas_abertas.length; i++){ 
		if(salas_abertas[i] == id) { 
			salas_abertas.splice(i, 1); 
        }
    }

	$('#select_sala').prop('disabled', false);
	$('#select_docente').prop('disabled', false);
	$('#select_turma').prop('disabled', false);
}

function removeOptions(selectElement) {
	var i, L = selectElement.options.length - 1;
	for(i = L; i >= 0; i--) {
		selectElement.remove(i);
	}
}

